import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Product } from 'src/app/models/product.model';
import { ProductsService } from 'src/app/services/products.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  newProduct: Product = {
    id: '',
    name: '',
    type: '',
    color: '',
    price: 0
  };

  constructor(private productService: ProductsService, private router: Router) {}

  addProduct() {
    this.productService.addProduct(this.newProduct)
      .subscribe({
        next: (product) => {}
        error: (response) 
      });
  }
}
<div class="container">
    <h1>Add new product</h1>
    <div class="row">
        <div class="col-6">
            <form #form="ngForm" (ngSubmit)="addProduct()">
                <div class="mb-3">
                    <label for="name" class="form-label">Product Name</label>
                    <input type="text" class="form-control" id="name" [(ngModel)] = "newProduct.name">
                </div>
                <div class="mb-3">
                    <label for="type" class="form-label">Type</label>
                    <input type="type" class="form-control" id="type" name="type" [(ngModel)] = "newProduct.type">
                </div>
                <div class="mb-3">
                    <label for="color" class="form-label">Color</label>
                    <input type="text" class="form-control" id="color" name="color" [(ngModel)] = "newProduct.color">
                </div>
                <div class="mb-3">
                    <label for="price" class="form-label">Price</label>
                    <input type="number" class="form-control" id="price" name="price" [(ngModel)] = "newProduct.price">
                </div>
                <button type="submit" class="btn btn-primary">Save</button>
            </form>
        </div>
    </div>
</div>
