import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { NgToastService } from 'ng-angular-popup';
import { Product } from 'src/app/models/product.model';
import { Warehouse } from 'src/app/models/warehouse.model';
import { ProductsService } from 'src/app/services/products.service';
import { WarehouseService } from 'src/app/services/warehouse.service';

@Component({
  selector: 'app-warehouse',
  templateUrl: './warehouse.component.html',
  styleUrls: ['./warehouse.component.css']
})
export class WarehouseComponent implements OnInit {
  products: Product[] = [];
  warehouses: Warehouse[] = [];

  selectedProduct!: Product;
  selectedWarehouse!: Warehouse;
  quantity!: number;


  constructor(private productsService: ProductsService, private warehouseService: WarehouseService, private toast: NgToastService, private router: Router) { }

  ngOnInit(): void {
    this.productsService.getAllProducts()
      .subscribe({
        next: (products) => {
          this.products = products;
        },
        error: (response) => {
          console.log(response);
        }
      });
    this.warehouseService.getAllWarehouses()
      .subscribe({
        next: (warehouse) => {
          this.warehouses = warehouse;
        },
        error: (response) => {
          console.log(response);
        }
      })
  }

  addProductToWarehouse(): void {
    this.warehouseService.addProductToWarehouse(this.selectedWarehouse.id, this.selectedProduct.id, this.quantity)
      .subscribe({
        next: () => {
          this.router.navigate(['reports'])
        },
        error: (response) => {
          console.log(response);
        }
      })
  }

}
