                      </tr>
                  </thead>
                  <tbody>
                      <tr *ngFor="let warehouse of warehouses">
                          <th>{{ warehouse.id }}</th>
                          <td>{{ warehouse.name }}</td>
                          <td>{{ warehouse.capacity }}</td>
