import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from 'src/app/models/product.model';
import { ProductsService } from 'src/app/services/products.service';

@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent implements OnInit{
  updateProductRequest: Product = {
    id: '',
    name: '',
    type: '',
    color: '',
    price: 0
  };
  constructor(private router: Router,
    private productService: ProductsService,
    private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe({
      next: (params) => {
        const id = params.get('id');

        if(id) {
          this.productService.getProduct(id)
            .subscribe({
              next: (response) => {
                this.updateProductRequest = response;
              }
            });
        }
      }
    })
  }

  updateProduct(){
    this.productService.updateProduct(this.updateProductRequest.id, this.updateProductRequest)
      .subscribe({
        next: (response) => {
          this.router.navigate(['products']);
        },
        error: (err) => {
          console.log(err);
        }
      });
  };
}
<div class="container-fluid">

    <div class="row">
        <div class="col-md-9 ms-sm-auto col-lg-10 px-md-4">
            <div class="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
                <h1 class="h2">Edit Product</h1>
                <div class="btn-toolbar mb-2 mb-md-0">

                </div>
            </div>
            <form #form="ngForm" (ngSubmit)="updateProduct()">
                <div class="mb-3">
                    <label for="id" class="form-label">Id</label>
                    <input type="text" class="form-control bg-light" id="id" name='id' readonly [(ngModel)] = "updateProductRequest.id">
                </div>
                <div class="mb-3">
                    <label for="name" class="form-label">Name</label>
                    <input type="name" class="form-control" id="name" name='name' [(ngModel)] = "updateProductRequest.name">
                </div>
                <div class="mb-3">
                    <label for="type" class="form-label">Type</label>
                    <input type="name" class="form-control" id="type" name="type" [(ngModel)] = "updateProductRequest.type">
                </div>
                <div class="mb-3">
                    <label for="color" class="form-label">Color</label>
                    <input type="name" class="form-control" id="color" name="color" [(ngModel)] = "updateProductRequest.color">
                </div>
                <div class="mb-3">
                    <label for="price" class="form-label">Price</label>
                    <input type="number" class="form-control" id="price" name="price" [(ngModel)] = "updateProductRequest.price">
                </div>
                <button type="submit" class="btn btn-primary">Update</button>
            </form>
        </div>
    </div>
</div>
